/*
 * 증감연산자
 * -------
 * 무조건 증가
 * int num1=10;   
      System.out.printf("%d \n", num1);
      num1++;
      System.out.printf("%d \n", num1);
      ++num1;
      System.out.printf("%d \n", num1);
      --num1;
      System.out.printf("%d \n", num1);
      num1--;
      System.out.printf("%d \n", num1);

 */
public class 연산자문제_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c='A';
		System.out.println(c++); // 출력하고 나중에 증가
		System.out.println(c+"1");
		// 증감연산자 : 제어문 => 반복문 , 횟수 구하기 , 크롤링
	}

}
